const long long INF = 1000;
const long long mod = 1e9 + 7;
typedef long long ll;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int  n =nums.size();
        vector<vector<long long>> dp(n,vector<long long>(51,-1));
        function<ll(int,int)> dfs = [&](int i,int j) {
            if(i==n) {
                return 1LL;
            }
            if(dp[i][j]!=-1) return dp[i][j];
            int k;
            if(i==0) k = INF;
            else k = nums[i-1]-j;
            long long res = 0;
            for(int c=0;c<=nums[i];c++) {
                if(c>=j && nums[i]-c<=k) {
                    res += dfs(i+1,c);
                    res %=mod;
                }
            }
            return dp[i][j] = res;
        };
        return (int)dfs(0,0);
    }
};
