class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        cache = {}
        def dfs(index, increasing, decreasing):
            if index == len(nums):
                return 1

            if (index, increasing, decreasing) in cache:
                return cache[(index, increasing, decreasing)]

            count = 0
            for i in range(max(0, increasing), nums[index] + 1):
                comp = nums[index] - i
                if comp > decreasing or comp < 0:
                    continue
                count = (count + dfs(index + 1, i, comp)) % MOD

            cache[(index, increasing, decreasing)] = count
            return count

        return dfs(0, float('-inf'), float('inf')) % MOD
