class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long mod=1000000007;
        int n=nums.size();
        vector<vector<long long>>dp(n,vector<long long>(51));
        for(int i=0;i<51;i++){
            if(i<=nums[0]){
                dp[0][i]=1;
            }
        }
        for(int i=1;i<n;i++){
            for(int p=0;p<=nums[i];p++){
                long long s=0;
                for(int k=0;k<=nums[i-1];k++){
                    if(k<=p && (nums[i-1]-k)>=(nums[i]-p)){
                        s+=dp[i-1][k];
                        s%=mod;
                    }
                }
                dp[i][p]=s;
            }
        }
        long long ans=0;
        for(int j=0;j<=50;j++){
            ans+=dp[n-1][j];
            ans%=mod;
        }
        return ans;
    }
};
