class Solution {
    int[] values;
    long[][][] dp;
    int size ;
    int MOD =(int)(1e9+7);
    public long solve(int index, int val1, int val2) {
        if (index >= size){
            return 1;
        }

        if (dp[index][val1][val2] != -1){
            return dp[index][val1][val2];
        }

        long result = 0;
        for (int el1 = val2; el1 >= 0; el1--) {
            for (int el2 = val1; el2 <= values[index]; el2++) {
                if (el2 + el1 == values[index]) {
                    result = (result % MOD + solve(index + 1, el2, el1) % MOD) % MOD;
                }
            }
        }

        return dp[index][val1][val2] = result;
    }
    public int countOfPairs(int[] values) {
        this.values = values;
        size = values.length;
        dp = new long[size+1][51][51];

        for (int i = 0; i < dp.length; i++) {
            for (int j = 0; j < 51; j++) {
                Arrays.fill(dp[i][j], -1);
            }
        }

        return (int)solve(0, 0, values[0]);
    }
}
