class Solution {
public:
  int countOfPairs(const vector<int>& nums) {
    int steps = nums.size();
    int tears = 1 + *max_element(nums.begin(), nums.end());
    int mod = 1e9 + 7;
    vector<vector<int64_t>> table(tears, vector<int64_t>(steps, 0));
    for (int i = 0; i <= nums.back(); ++i) {
      table[i][steps - 1] = 1;
    }
    for (int step = steps - 2; step >= 0; --step) {
      for (int v = 0; v <= nums[step]; ++v) {
        for (int vn = 0; vn <= nums[step + 1]; ++vn) {
          if (v <= vn && nums[step] - v >= nums[step + 1] - vn) {
            table[v][step] += table[vn][step + 1];
            table[v][step] %= mod;
          }
        }
      }
    }
    int64_t res = 0;
    for (int i = 0; i < tears; ++i) {
      res += table[i][0];
      res %= mod;
    }
    return res;
  }
};
