class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7

        @lru_cache(None)
        def rec(ind, ma, mi):
            if ind == len(nums):
                return 1

            num = 0
            for i in range(ma + 1):
                if i <= nums[ind] and nums[ind] - i >= mi:
                    num += rec(ind + 1, i, nums[ind] - i)
                    num %= mod

            return num

        n = len(nums)
        ans = 0
        for i in range(nums[0] + 1):
            ans += rec(1, i, nums[0] - i)
            ans %= mod

        return ans
