class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n,memo = len(nums), {}
        v = 1

        def dp(i, pa, pb):
            if i == n:
                return 1

            if (i, pa, pb) in memo:
                return memo[(i, pa, pb)]

            c = 0
            max_val1 = nums[i]
            for a in range(max_val1 + 1):
                b = nums[i] - a
                if (a >= pa) and (b <= pb):
                    c = (c + dp(i + 1, a, b)) % MOD

            memo[(i, pa, pb)] = c
            return c
        return dp(0, 0, float('inf'))
