class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        memo = {}

        def backtrack(i, prev1, prev2) -> int:
            if i == len(nums):
                return 1

            if (i, prev1, prev2) in memo:
                return memo[(i, prev1, prev2)]

            count = 0
            for a in range(nums[i] + 1):
                b = nums[i] - a
                if (i == 0 or prev1 <= a) and (i == 0 or prev2 >= b):
                    count += backtrack(i + 1, a, b)
                    count %= MOD

            memo[(i, prev1, prev2)] = count
            return count

        return backtrack(0, 0, float('inf'))
