class Solution {
public:
    const int mod=1e9+7;
    int dp[2001][52][52];
    int solve(int i,vector<int>&nums,int a1,int a2){
        if(i==nums.size()) return 1;
        if(dp[i][a1][a2]!=-1) return dp[i][a1][a2];
        int ans=0;
        for(int j=0;j<=nums[i];j++){
            int num1=j;
            int num2=nums[i]-j;
            if(num1>=a1 and a2>=num2){
                ans=(ans+solve(i+1,nums,num1,num2))%mod;
            }
        }
        return dp[i][a1][a2]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        int n=nums.size();
        return solve(0,nums,0,51);
    }
};
