class Solution {
public:
    int mod=1e9+7;
    int solve(int ind,int n,vector<int>&nums,int arr1,int arr2){
         if(ind>=n){
             return 1;
         }
        int num=nums[ind], cnt=0;
        for(int i=0;i<=num;i++){
            if(i>=arr1 and (num-i)<=arr2){
                cnt+=solve(ind+1,n,nums,i,num-i)%mod;
            }
        }
        return cnt%mod;
    }
    int memoization(int ind,int n,vector<int>&nums,int arr1,int arr2,vector<vector<vector<long long>>>&dp){
         if(ind>=n){
             return 1;
         }
        if(dp[ind][arr1][arr2]!=-1) return dp[ind][arr1][arr2];
        int num=nums[ind], cnt=0;
        for(int i=0;i<=num;i++){
            if(i>=arr1 and (num-i)<=arr2){
                cnt=(cnt+memoization(ind+1,n,nums,i,num-i,dp)%mod)%mod;
            }
        }
        return dp[ind][arr1][arr2]=(cnt%mod);
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int arr1=0,arr2=51;
        // return solve(0,n,nums,arr1,arr2);

        vector<vector<vector<long long>>>dp(n+1,vector<vector<long long>>(52,vector<long long>(52,-1)));

        return memoization(0,n,nums,arr1,arr2,dp)%mod;
    }
};
