class Solution {
public:
    int mod = 1e9 + 7;
    int solve(vector<int>& nums,int idx,int prev,int last_ele,vector<vector<vector<int>>> &dp){
        if(idx>=nums.size()) return 1;

        if(dp[idx][prev][last_ele]!=-1) return dp[idx][prev][last_ele];

        long long int result = 0;

        for(int i=prev;i<=nums[idx];i++){
            if(nums[idx]-i <= last_ele) result = (result%mod + solve(nums,idx+1,i,nums[idx]-i,dp)%mod)%mod;
        }

        return dp[idx][prev][last_ele] = result;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n+1,vector<vector<int>>(51,vector<int>(52,-1)));
        return solve(nums,0,0,51,dp);
    }
};
