class Solution {
    int rec(int n,int prev,int prev2,vector<int>&nums,vector<vector<int>>&dp){
        if(n>=nums.size())return 1;
        if(dp[n][prev]!=-1)return dp[n][prev];
        int cnt=0;
        for(int i=prev;i<=nums[n];i++){
            if(nums[n]-i<=prev2)cnt=(cnt+rec(n+1,i,nums[n]-i,nums,dp))%(1000000007);
        }
        return dp[n][prev]=cnt;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>>dp(n+1,vector<int>(2000,-1));
        return rec(0,0,1e9,nums,dp);
    }
};
