class Solution {
public:
const int mod = (int)1e9+7;
    // map<pair<int,pair<int,int>>,int> dp;
    int dp[2002][51][60];
    int n;
    int solve(int idx, int l1, int l2, vector<int>&nums){
        if(idx >= n) {
            return 1;
        }

        if(l1 != -1 && dp[idx][l1][l2] != -1) return dp[idx][l1][l2];
        // if(dp.count({idx,{l1,l2}})) return dp[{idx,{l1,l2}}];


        int ways = 0;
        for(int i=max(l1,0);i<=50;i++){
            for(int j=min(nums[idx],l2);j>=0;j--){
                if(i+j == nums[idx]){
                    ways += solve(idx+1,i,j,nums)%mod;
                    ways %= mod;
                }
            }
        }
        if(l1 == -1) 
        return ways;

        return dp[idx][l1][l2] = ways;

    }
    int countOfPairs(vector<int>& nums) {
        // dp.clear();
        memset(dp,-1,sizeof(dp));
        n = nums.size();
        return solve(0,-1,55,nums);
    }
};
