class Solution {
public:
    int dp[51][51][2001];
    int mod=1000000007;
    int help(int up,int down,int ind,vector<int> &nums)
    {
        if(ind==nums.size())
            return 1;

        if(dp[up][down][ind]!=-1)
            return dp[up][down][ind];
        int ans=0;
        for(int i=0;i<=nums[ind];i++)
        {
            if(up<=i&&down>=nums[ind]-i)
                (ans+=help(i,nums[ind]-i,ind+1,nums)%mod)%=mod;
        }
        return dp[up][down][ind]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int ans=0;
        memset(dp,-1,sizeof(dp));
        for(int i=0;i<=nums[0];i++)
            (ans+=help(i,nums[0]-i,1,nums)%mod)%=mod;
        return ans;
    }
};
