class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        res = 0
        cache = {}

        # for i in range(nums[-1]):
        #     cache[(len(nums)-1, i, nums[-1] - i)] = 1
        def dfs(index, min_val, max_val):
            if index == len(nums) - 1:
                total = nums[-1]
                count = 0
                for i in range(max(min_val, total-max_val), total + 1):
                    if total - i <= max_val:
                        count += 1
                cache[(index, min_val, max_val)] = count
                return count
            if (index, min_val, max_val) in cache:
                return cache[(index, min_val, max_val)]
            total = nums[index]
            results = 0
            for i in range(max(total - max_val, min_val), total + 1):
                results += dfs(index + 1, i, total - i)
            cache[(index, min_val, max_val)] = results
            return results

        res = dfs(0, 0, nums[0])

        return res % (10 **9 + 7)
