class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<int> arr1(n), arr2(n);
        vector<vector<vector<int>>> memo(n, vector<vector<int>>(51, vector<int>(51, -1)));
        int res = backtrack(0, nums, arr1, arr2, memo, -1, 51);
        return res;
    };
private:
    const int MOD = 1e9 + 7;

    int backtrack(int i, const vector<int>& nums, vector<int>& arr1, vector<int>& arr2, vector<vector<vector<int>>>& memo, int prev1, int prev2) {
        if (i == nums.size()) {
            return 1;
        }
        if (prev1 != -1 && memo[i][prev1][prev2] != -1) {
            return memo[i][prev1][prev2];
        }
        int count = 0;
        for (int j = 0; j <= nums[i]; j++) {
            int c = nums[i] - j;
            if ((i == 0 || prev1 <= j) && (i == 0 || prev2 >= c)) {
                arr1[i] = j;
                arr2[i] = c;
                count = (count + backtrack(i + 1, nums, arr1, arr2, memo, j, c)) % MOD;
            }
        }
        if (prev1 != -1) {
            memo[i][prev1][prev2] = count;
        }
        return count;
    }
};
