class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        memo = {}

        def dfs(i, prev1, prev2):
            if i == n:
                return 1

            if (i, prev1, prev2) in memo:
                return memo[(i, prev1, prev2)]

            total = 0

            for arr1_i in range(prev1, nums[i] + 1):
                arr2_i = nums[i] - arr1_i
                # Ensure arr2[i] <= prev2
                if arr2_i <= prev2:
                    total += dfs(i + 1, arr1_i, arr2_i)
                    total %= MOD

            memo[(i, prev1, prev2)] = total
            return total

        return dfs(0, 0, nums[0])
