constexpr int base = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        auto memo = vector(n, vector<int>(1001));
        for (int v = 0; v <= nums[0]; ++v) {
            memo[0][v] = 1;
        }
        for (int i = 1; i < n; ++i) {
            int l0 = 0;
            int l1 = max(0, nums[i] - nums[i - 1]);
            int s = 0;
            for (int v = l1; v <= nums[i]; ++v, ++l0) {
                s = (s + memo[i - 1][l0]) % base;
                memo[i][v] = s;
            }
        }
        int res = 0;
        for (int v = 0; v <= nums[n - 1]; ++v) {
            res = (res + memo[n - 1][v]) % base;
        }
        return res;
    }
};
