class Solution {
    int n;
    const int MOD = 1e9 + 7;
    int dp[2001][53];
    int fn(int ind,int prev1,int prev2,vector<int>& nums){
        if(ind==n)
        return 1;
        if(dp[ind][prev1+1]!=-1)
        return dp[ind][prev1+1];
        int ans=0;
        for(int i=0;i<=nums[ind];i++){
            if(prev1==-1 || prev1>=i && prev2<=(nums[ind]-i)){
                ans=(ans+fn(ind+1,i,nums[ind]-i,nums))%MOD;
            }
        }
        return dp[ind][prev1+1]=ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        memset(dp,-1,sizeof(dp));
        return fn(0,-1,-1,nums);
    }
};
