#define ll long long

class Solution {
public:
    const int mod = 1e9 + 7;
    int memo[51][20001];
    int rec(int prev, int i, vector<int>& nums) {
        if (i == nums.size()) return 1;
        ll count = 0;
        if (memo[prev][i] != -1) return memo[prev][i];
        for (int curr = prev; curr <= nums[i]; curr++) {
            if (i != 0 && nums[i - 1] - prev < nums[i] - curr) continue;
            count = (count + rec(curr, i + 1, nums)) % mod;
        }

        return memo[prev][i] = count;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(memo, -1, sizeof(memo));

        return rec(0, 0, nums); 
    }
};
