class Solution {
public:

    int MOD=1e9+7;

    int dpHelp(int idx,int prev1,int prev2,vector<int>&nums,vector<vector<vector<int>>>&dp){
        if(idx==nums.size()) return 1;
        if(dp[idx][prev1][prev2]!=-1) return dp[idx][prev1][prev2];
        int count=0;
        for(int i=prev1;i<=nums[idx];i++){
            int j=nums[idx]-i;
            if(j<=prev2){
                count=(count+dpHelp(idx+1,i,j,nums,dp))%MOD;
            }
        }
        return dp[idx][prev1][prev2]= count;
    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(52,vector<int>(52,-1)));
        return dpHelp(0,0,51,nums,dp);
    }
};
