class Solution {
private:
    vector<vector<vector<long long>>> memo;
    vector<int> values;
    const long long mod_val = 1e9 + 7;

    long long solve(int index, int val1, int val2) {
        if (index >= values.size()) return 1;
        if (memo[index][val1][val2] != -1) return memo[index][val1][val2];

        long long result = 0;
        for (int i = val1; i <= values[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[index]) {
                    result += solve(index + 1, i, j);
                    result %= mod_val;
                }
            }
        }
        return memo[index][val1][val2] = result;
    }

public:
    int countOfPairs(vector<int>& inputValues) {
        values = inputValues;
        int size = values.size();
        memo = vector<vector<vector<long long>>>(size, vector<vector<long long>>(51, vector<long long>(51, -1)));
        return solve(0, 0, values[0]);
    }
};
