class Solution {
public:
    int M = 1e9 + 7;
    int solve(int ind,vector<int>& nums,int prevInc,int prevRev,vector<vector<vector<int>>> &dp){
        if(ind == nums.size()) return 1;

        if(dp[ind][prevInc][prevRev] != -1) return dp[ind][prevInc][prevRev];

        int curr = nums[ind];

        int res = 0;

        for(int i=0;i<=curr;i++){
            if(i >= prevInc && (curr - i) <= prevRev){
                long long temp = solve(ind+1,nums,i,curr-i,dp);
                res = (res%M + temp%M)%M;
            }
        }

        return dp[ind][prevInc][prevRev] = res;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        return solve(0,nums,0,50,dp);
    }
};
