class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int N=51;
        const int mod=1e9+7;
        nums.insert(nums.begin(),N);
        int n=nums.size();
        vector<long long> dp(N,0);
        dp[0]=1;
        for(int i=1;i<n;i++){
            vector<long long> temp(N,0);
            for(int curr=0;curr<N;curr++){
                for(int prev=0;prev<N;prev++){
                    if(curr>=prev && nums[i]-curr<=nums[i-1]-prev && curr<=nums[i] && prev<=nums[i-1]){
                        temp[curr]=(temp[curr]+dp[prev])%mod;
                    }
                }
            }
            dp=temp;
        }
        long long ans=0;
        for(int i=0;i<N;i++) ans=(ans+dp[i])%mod;
        return ans;
    }
};
