#define MOD 1000000007
#define MAX_VAL 51

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long long>>> dp(n + 1, vector<vector<long long>>(MAX_VAL, vector<long long>(MAX_VAL, -1)));

        return computePairs(nums, 0, 0, nums[0], dp);
    }

private:
    long long computePairs(const vector<int>& nums, int idx, int minVal, int maxVal, vector<vector<vector<long long>>>& dp) {
        if (idx >= nums.size()) return 1;
        if (dp[idx][minVal][maxVal] != -1) return dp[idx][minVal][maxVal];

        long long totalCount = 0;
        for (int i = minVal; i <= nums[idx]; ++i) {
            for (int j = maxVal; j >= 0; --j) {
                if (i + j == nums[idx]) {
                    totalCount = (totalCount + computePairs(nums, idx + 1, i, j, dp)) % MOD;
                }
            }
        }

        return dp[idx][minVal][maxVal] = totalCount;
    }
};
