class Solution {
	private static int U = 50;
	private static int MOD = 1_000_000_007;
	private int[] nums;
	private int dp(int i, int last1, int last2, int[][][] memo) {
		int n = nums.length;
		if (i>=n) {
			return 1;
		}
		if (memo[i][last1][last2]!=-1) return memo[i][last1][last2];
		int x = nums[i];
		// int jMin = Math.min(last1, x-last2);
		// int kMax = Math.max(last2, x-last1);
		int res = 0;
		for (int j = Math.max(last1, x-last2); j <= x; ++j) {
			int k = x-j;
			res += dp(i+1, j, k, memo);
			res %= MOD;
		}
		return memo[i][last1][last2] = res;
	}
    public int countOfPairs(int[] nums) {
        final int n = nums.length;
		this.nums = nums;
		int ans = 0;
		// int[][][] f = new int[n+1][U+1][U+1];
		// for (int i = 0; i < n; ++i) {
		// 	int x = nums[i];
		// 	for (int j = 0; j <= x; ++j) {
		// 		int k = x-j;
		// 		f[]
		// 	}
		// }
		int[][][] memo = new int[n][U+1][U+1];
		for (int i = 0; i < n; ++i) {
			for (int j = 0; j <= U; ++j) {
				Arrays.fill(memo[i][j], -1);
			}
		}
		ans = dp(0, 0, U, memo);
		return ans;
    }
}
