int mod=1e9+7;
class Solution {
private:
    long doo(int ind,int prev1,int prev2,vector<int>&nums,vector<vector<vector<int>>>&dp){
        if(ind==nums.size()){
            // cout<<nums.size()<<"\n";
            return 1;
        }
        if(dp[ind][prev1][prev2]!=-1){
            return dp[ind][prev1][prev2];
        }

        long ans=0;
        for(int i=prev1;i<=nums[ind];i++){
            int sec=nums[ind]-i;
            if(sec<=prev2 && sec>=0){
                // cout<<ind<<" "<<i<<" "<<sec<<"\n";
                ans+=doo(ind+1,i,sec,nums,dp);
                ans%=mod;
            }
        }
        return dp[ind][prev1][prev2]= ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(55,vector<int>(55,-1)));
        long ans=0;
        ans=doo(0,0,nums[0],nums,dp);

        return ans;
    }
};
