class Solution {
public:

    int mod = 1e9+7;
    vector<vector<int>>mem;

    int find(vector<int>& nums, int v1, int v2, int idx) {
        if(idx == nums.size()) {
            return 1;
        }
        if(mem[v1+1][idx] != -1)
            return mem[v1+1][idx];
        int num = nums[idx];
        int ans = 0;
        for(int k=0; k<=num; k++) {
            if(k>=v1 && num-k<=v2) {
                ans = (ans%mod + find(nums, k, num-k, idx+1)%mod) %mod;
            }
        }
        return mem[v1+1][idx] = ans;
    }

