class Solution {
    long output = 0l;
    Integer dp[][][];
    public int countOfPairs(int[] nums) {
        dp = new Integer[nums.length][52][52];
        return find(0,51,nums,0)%(1000000007);
        //return (int)(output%(1000000007));
    }
    public int find(int a,int b,int nums[],int index){
        //System.out.println(a+"   "+b+"   "+index);
        if(index == nums.length) { return 1; }
        if(dp[index][a][b] != null) return dp[index][a][b];
        int ret = 0;
        for(int i=0;i<=nums[index];i++){
            int jk = i,kl = nums[index]-i;
            //System.out.println(jk+"   "+kl+"   "+a+"   "+b);
            if(jk>=a && kl<=b) ret = (ret+find(jk,kl,nums,index+1))%(1000000007);
            //else break;
        }
        return dp[index][a][b] = ret%(1000000007);
    }
}
