class Solution {
public:
    int n;
    int memo[2000][51][51];
    long long mod = 1e9+7;
    int recursion(vector<int>& nums,int idx,int prev1, int prev2){
        if(idx>=n) return 1;
        if(memo[idx][prev1][prev2]!=-1) return memo[idx][prev1][prev2];
        long long  ans = 0;
        for(int i=prev1;i<=nums[idx];i++){
            if(nums[idx]-i>prev2) continue;
            ans += recursion(nums,idx+1,i,nums[idx]-i);
            ans %= mod;
        }
        return memo[idx][prev1][prev2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        memset(memo,-1,sizeof(memo));
        return recursion(nums,0,0,50);
    }
};
