#define ll long long
class Solution {
    ll mod = 1e9 + 7;
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>> (51, vector<ll>(51, -1)));

        function<ll(int, int, int)> f = [&](int ind, int val1, int val2) -> ll {
            if(ind >= n) return 1;
            ll res = 0;
            if(dp[ind][val1][val2] != -1) return dp[ind][val1][val2];

            for(int i=val1;i<=nums[ind];i++) {
                for(int j=val2;j>=0;j--) {
                    if(i+j == nums[ind]) {
                        res += f(ind+1, i, j);
                        res = res % mod;
                    }
                }
            }
            return dp[ind][val1][val2] = res;  
        };
        return f(0, 0, nums[0]);
    }
};
