import java.util.Arrays;

public class Solution {
    private static final int MOD = 1_000_000_007;
    private long[][][] memo;

    public int countOfPairs(int[] values) {
        int size = values.length;
        memo = new long[size][51][51];
        for (long[][] matrix : memo) {
            for (long[] row : matrix) {
                Arrays.fill(row, -1);
            }
        }

        return (int) solve(0, 0, values[0], values);
    }

    private long solve(int index, int val1, int val2, int[] values) {
        if (index >= values.length) return 1;
        if (memo[index][val1][val2] != -1) return memo[index][val1][val2];

        long result = 0;
        for (int i = val1; i <= values[index]; ++i) {
            for (int j = val2; j >= 0; --j) {
                if (i + j == values[index]) {
                    result = (result + solve(index + 1, i, j, values)) % MOD;
                }
            }
        }

        return memo[index][val1][val2] = result;
    }
}
