class Solution {
     long long mod = 1e9 + 7;
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long long>>> dp(n, vector<vector<long long>>(51, vector<long long>(51, -1)));


        return solve(0, 0, nums[0], nums, dp) % mod; 
    }

private:
    long long solve(int index, int arr1_val, int arr2_val, vector<int>& nums, vector<vector<vector<long long>>>& dp) {
        if (index >= nums.size()) return 1;

        if (dp[index][arr1_val][arr2_val] != -1) return dp[index][arr1_val][arr2_val];

        long long result = 0;
        int i = arr1_val;

        while (i <= nums[index]) {
            int j = arr2_val;
            while (j >= 0) {
                if (i + j == nums[index]) {
                    result = (result + solve(index + 1, i, j, nums, dp)) % mod;
                }
                --j;
            }
            ++i;
        }

        return dp[index][arr1_val][arr2_val] = result;
    }
};
