class Solution {
public:
    int mod=1e9+7;
    int fn(vector<int>&nums,int i,int gt,int lt,vector<vector<vector<int>>>&dp){
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][gt][lt]!=-1){
            return dp[i][gt][lt];
        }
        int ans=0;
        for(int a=gt;a<=nums[i];a++){
            int b=nums[i]-a;
            if(b<=lt){
                ans=(ans+fn(nums,i+1,a,b,dp))%mod;
            }
        }
        return dp[i][gt][lt]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        // vector<vector<vector<int>>>dp(n,vector<int>(51,vector<int>(51,-1)));
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));

        return fn(nums,0,0,50,dp)%mod;
    }
};
