class Solution {
public:
    int mod = 1e9+7;
    vector<vector<vector<long long> > >dp;
    //curr1 ->  increasing
    //curr2 -> decreasing
    long long func(int index, int curr1, int curr2, vector<int>& nums){
        if (index == nums.size())return 1;
        if (dp[index][curr1][curr2]!=-1)return dp[index][curr1][curr2];
        long long ans = 0;
        int tempCurr1 = curr1;
        int tempCurr2 = nums[index] - curr1;
        while (tempCurr2>curr2 && tempCurr2>=0 && tempCurr1<=nums[index]){
            tempCurr2--;
            tempCurr1++;
        }
        while(tempCurr1<=nums[index] && tempCurr2>=0 && tempCurr2<=curr2 && tempCurr1>=curr1){
            ans+=func(index+1, tempCurr1, tempCurr2, nums);
            ans%=mod;
            tempCurr1++;
            tempCurr2--;
        }
        dp[index][curr1][curr2] = ans;
        return ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int nax = 0;
        for(int i=0;i<n;i++)nax=max(nax,nums[i]);
        dp = vector<vector<vector<long long> > >(n+1, vector<vector<long long> >(nax+1, vector<long long>(nax+1, - 1)));
        long long ans = 0;
        int curr1 = 0, curr2 = nums[0];
        while(curr1<=nums[0]){
            ans+=func(1, curr1, curr2, nums);
            curr1++;
            curr2--;
            ans%=mod;
        }
        return ans;
    }
};
