class Solution
{
public:
    long long M = 1e9 + 7;
    long long solve(int ind, int l, int r, vector<int> &nums, vector<vector<vector<long long>>> &dp)
    {
        if (ind >= nums.size()){
            return 1;
        }

        if (dp[ind][l][r] != -1){
            return dp[ind][l][r];
        }

        long long ans = 0;
        for (int i = l; i <= nums[ind]; i++)
        {
            for (int j = r; j >= 0; j--)
            {
                if (i + j == nums[ind])
                {
                    ans = (ans + solve(ind + 1, i, j, nums, dp)) % M;
                }
            }
        }
        return dp[ind][l][r] = ans;
    }
    int countOfPairs(vector<int> &nums)
    {
        vector<vector<vector<long long>>> dp(nums.size(), vector<vector<long long>>(55, vector<long long>(55, -1)));
        return solve(0, 0, nums[0], nums, dp);
    }
};
