using ll = long long;
class Solution {
public:
    ll dp[2005][1005];
    int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return dfs(0, 0, nums);
    }

    ll dfs(int i, int pre1, vector<int>& nums) {
        int n = nums.size();
        if (n == i)
            return 1;
        if (~dp[i][pre1])
            return dp[i][pre1];
        int pre2 = i == 0 ? 1001 : nums[i-1] - pre1;
        int val = 0;
        for (int x = pre1; x <= nums[i]; x++) {
            int y = nums[i] - x;
            if (y > pre2)
                continue;
            if (y < 0)
                break;
            val = (val + dfs(i+1, x, nums)) % MOD;
        }
        return dp[i][pre1] = val;
    }
};
