class Solution {
    vector<vector<vector<int>>> DP;
    long mod = 1e9+7;

    long solve(int ii, int a, int b, vector<int>& nums) {
        int k = a + 1, l = b + 1;
        if (DP[ii][k][l] != -1) return DP[ii][k][l];

        if (ii == nums.size()) return DP[ii][k][l] = 1;
        long ans = 0;

        if (a == -1) a = 0, b = nums[ii];

        for (int i = a; i <= nums[ii]; i++) {
            int j = nums[ii] - i;

            if (j <= b) ans = (solve(ii + 1, i, j, nums) + ans) % mod;
        }

        return DP[ii][k][l] = ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        DP = vector<vector<vector<int>>>(n + 1, (vector<vector<int>>(52, vector<int>(52, -1))));
        return solve(0, -1, -1, nums);
    }
};
