// Ganpati Bappa Morya
class Solution {
public:
    const int MOD = 1e9 + 7;

    // memo fucntion to calculate
    long long int solveUsingMemo(int index, int val1, int val2, vector<int>& values, vector<vector<vector<long long int>>>& memo) {
        if (index >= values.size()) return 1;

       // already present
        if (memo[index][val1][val2] != -1) return memo[index][val1][val2];

        long long int ans = 0;
        // ganpati bappa morya
        for (int i = val1; i <= values[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[index]) {
                    ans += solveUsingMemo(index + 1, i, j, values, memo);
                    ans %= MOD;
                }
            }
        }

        // store answer
        return memo[index][val1][val2] = ans;
    }

    int countOfPairs(vector<int>& values) {
        int size = values.size();
        vector<vector<vector<long long int>>> memo(size, vector<vector<long long int>>(51, vector<long long int>(51, -1)));
        // long long mod_val = 1e9 + 7;
        // return solveUsingMemo(0, 0, values[0], values, memo);
        return solveUsingMemo(0, 0, values[0], values, memo);
    }
};
