class Solution {
    int memo[51][51][2001];
    int mod;
public:
    int countOfPairs(vector<int>& nums) {
        mod = 1000000007;
        for(int i=0;i<51;++i){
            for(int j=0;j<51;++j){
                for(int k=0;k<2001;++k){
                    memo[i][j][k]=-1;
                }
            }
        }
        int x = nums[0];
        int result=0;
        for(int i=0;i<=x;++i){
            result=(result+(tell(i, x-i, 1, nums))%mod)%mod;
        }
        return result;
    }

    int tell(int preInc, int preDec, int index, vector<int>& nums){
        if(index>=nums.size()){
            return 1;
        }
        if(memo[preInc][preDec][index]!=-1){
            return memo[preInc][preDec][index];
        }
        int target = nums[index];
        int result = 0;
        for(int i=preInc;i<=target;++i){
            int nextInc = i;
            int nextDec = target-i;
            if(preInc<=nextInc && preDec>=nextDec){
                int temp = tell(nextInc, nextDec, index+1, nums);
                if(temp!=0){
                    result=(result+(temp)%mod)%mod;
                }
            }
        }
        return memo[preInc][preDec][index]=result;
    }
};
