class Solution {
public:
    const long long mod = 1e9 + 7;
    int dp[2001][51];

    long long solve(int i, int last, vector<int>& nums) {
        if (i == nums.size()) {
            return 1;  
        }
        if (dp[i][last] != -1) {
            return dp[i][last];
        }
        long long sum = 0;
        for (int j = last; j <= nums[i]; j++) {
            int arr2_val = nums[i] - j;
            if (i == 0 || arr2_val <= (nums[i-1] - last)) {
                sum += solve(i + 1, j, nums);
                sum %= mod;
            }
        }
        return dp[i][last] = sum;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(0, 0, nums);
    }
};
