class Solution {
public:

    // int answer = 0;
    const int mod = 1000000007;
    vector<vector<int>> dp;
    int count(int i, int prev_val, vector<int>& nums, int& n){
        if(i == n){
            return 1;
            // answer++;
            // answer %= 1000000007;
            // return;
        }
        if(dp[i][prev_val] != -1){
            return dp[i][prev_val];
        }
        int ans=0;
        if(prev_val >= 0 && nums[i-1] >= prev_val){
            for(int num = prev_val; num <= nums[i]; num++){
                if(nums[i]-num <= nums[i-1]-prev_val){
                    ans += count(i+1,num,nums,n);
                    ans %= mod;
                }
            }
        }
        return dp[i][prev_val] = ans;

    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        dp.assign(n+1,vector<int>(60,-1));
        int answer=0;
        for(int num = 0;num<=nums[0];num++){
            answer += count(1,num,nums,n);
            answer %= mod;
        }
        return answer;
    }
};
