class Solution {
public:
    using ll = long long;
    int mod = 1e9+7;
    int dp[2001][55][65];
    ll solve(vector<int> &nums, int idx=0, int prevInc=-1, int prevDcr=60){
        if(idx==nums.size())
            return 1LL;
        if(dp[idx][prevInc+1][prevDcr]!=-1)
            return dp[idx][prevInc+1][prevDcr];

        ll x = 0LL;
        for(int num=0; num<=nums[idx]; num++){
            if(num>=prevInc && (nums[idx]-num)<=prevDcr)
                x = (x + solve(nums, idx+1, num, nums[idx]-num))%mod;
        }

        return dp[idx][prevInc+1][prevDcr] = x%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(nums);
    }
};
