class Solution {
public:
    int n;
    const int mod = 1e9 + 7;
    vector<vector<vector<int>>> dp;
    int f(int index, int prev1, int prev2, vector<int>& nums) {
        if (index >= n) return 1;
        if (prev1 >= 0 && prev1 <= 50 && prev2 >= 0 && prev2 <= 50 && dp[index][prev1][prev2] != -1)
            return dp[index][prev1][prev2];

        int curr = nums[index];
        int ans = 0;
        for (int i = 0; i <= curr; i++) {
            int curr1 = i;
            int curr2 = curr - i;
            if (prev1 == -1 && prev2 == 51 || curr1 >= prev1 && curr2 <= prev2) {
                ans = (ans + f(index + 1, curr1, curr2, nums)) % mod;
            }
        }

        if (prev1 >= 0 && prev1 <= 50 && prev2 >= 0 && prev2 <= 50) {
            dp[index][prev1][prev2] = ans;
        }

        return ans;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return f(0, -1, 51, nums);
    }
};
