# import numpy as np
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        n = len(nums)
        dp = [[0] * 51 for _ in range(n)] # dp[i][j] is # of pairs up to i with arr_1[i] = j
        for i in range(nums[0] + 1):
            dp[0][i] = 1
        for i in range(1, n):
            for j in range(nums[i] + 1):
                for k in range(min(j + 1, nums[i - 1] + 1)):
                    if nums[i] - j <= nums[i - 1] - k:
                        dp[i][j] += dp[i - 1][k]
                        dp[i][j] %= MOD
        return sum(dp[-1]) % MOD
