class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(55, 0));

        for(int k = 0; k <= nums[0]; k++) {
            dp[0][k] = 1;
        }

        int mod = 1000000007;

        for(int i = 1; i < n; i++) {
            for(int k = 0; k <= nums[i]; k++) {
                int i1 = k;
                int i2 = nums[i] - i1;
                for(int k2 = 0; k2 <= k; k2++) {
                    int j1 = k2;
                    int j2 = nums[i - 1] - j1;

                    if(j2 >= i2) {
                        dp[i][k] = (dp[i][k] + dp[i - 1][k2]) % mod;
                    }
                }
            }
        }

        int result = 0;
        for(int i = 0; i <= 54; i++) {
            result = (result + dp[n - 1][i]) % mod;
        }

        return result;
    }
};
