class Solution {
    int mod = 1e9 + 7;

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        std::vector<std::vector<std::vector<int>>> dp(
            n, std::vector<std::vector<int>>(51, std::vector<int>(51, 0)));

        // initialize the last one
        int last_target = nums.back();
        for (int i = 0; i <= last_target; ++i) {
            dp[n - 1][i][last_target - i] = 1;
        }

        for (int i = n - 2; i >= 0; --i) {
            // printf("\ni:%d\n", i);
            int target = nums[i];
            int a = 0; // possible value chosen for arr1

            while (a <= target) {
                int b = target - a; // corresponding value for arr2
                // printf("a:%d, b:%d\n", a, b);

                int j = a, k = b;
                int sum = j + k, diff = nums[i + 1] - sum;
                if (diff > 0) {
                    j += diff;
                } else {
                    k += diff;
                }

                while (j <= 50 && k >= 0) {
                    // printf("j:%d, k:%d\n", j, k);
                    dp[i][a][b] += dp[i + 1][j][k];
                    dp[i][a][b] %= mod;
                    ++j;
                    --k;
                }

                ++a;
            }
        }

        int res = 0;
        for (int i = 0; i <= nums[0]; ++i) {
            res += dp[0][i][nums[0] - i];
            res %= mod;
        }

        return res;
    }
};
