class Solution {
public:

    int mod=1000000007; 
    int func(vector<int>&nums,int idx,int prev,vector<vector<int>>&dp){
        if(idx==nums.size()){
            return 1;
        }
        if(dp[idx][prev]!=-1){
            return dp[idx][prev];
        }
        int ans=0;
        for(int i=0;i<=nums[idx];i++){
            if(idx!=0){
                if(i>=prev){
                    if(nums[idx]-i <= nums[idx-1]-prev){
                        ans=(ans+func(nums,idx+1,i,dp))%mod;
                    }
                }
            }
            else{
                ans=(ans+func(nums,idx+1,i,dp))%mod;
            }
        }
        return dp[idx][prev]=ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int ans=0;

        // for(int i=0;i<=nums[0];i++){
            vector<vector<int>>dp(n+1,vector<int>(51,-1));
            ans=(ans+func(nums,0,0,dp))%mod;
        // }
        return ans;
    }
};
