class Solution {
public:

    static constexpr int MOD = 1e9 + 7;

    int dp[2001][51][51];

    int dfs(vector<int> &nums, int idx, int p_n1 = 0, int p_n2 = 50)
    {
        const int n = nums.size();
        if(n == idx) return 1;

        int res = 0;

        if(dp[idx][p_n1][p_n2] != -1) return dp[idx][p_n1][p_n2];

        for(int i = 0; i <= nums[idx]; i++){
            int n1 = i;
            int n2 = nums[idx] - n1;


            if(n1 >= p_n1 && p_n2 >= n2)
            {
                res = (res + dfs(nums, idx +1, n1, n2))%MOD;
            }
        }

        return dp[idx][p_n1][p_n2] = res;
    }

    int countOfPairs(vector<int>& nums) {
        const int n = nums.size();

        memset(dp, -1, sizeof(dp));
        return dfs(nums, 0);
    }
};
