class Solution {
public:
    int mod=1e9+7;
    int dp[2001][51][51];
    int count(int ind,int prev1,int prev2,vector<int> &arr)
    {
        if(ind==arr.size())
            return 1;

        if(dp[ind][prev1][prev2]!=-1)
            return dp[ind][prev1][prev2];

        int c=0;
        for(int x1=prev1;x1<=arr[ind];x1++)
        {
            int x2=arr[ind]-x1;
            if(x2<=prev2)
                 c=(c+count(ind+1,x1,x2,arr))%mod;
        }
       return dp[ind][prev1][prev2]=c;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return count(0,0,50,nums);
    }
};
